import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProfileDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserProfile();
  }, [id]);

  const fetchUserProfile = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/users/${id}`);
      setUser(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRequestSwap = () => {
    navigate(`/request/${id}`);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">User Not Found</h1>
          <button
            onClick={() => navigate('/')}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
          >
            Back to Home
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <button
              onClick={() => navigate('/')}
              className="text-blue-600 hover:text-blue-700 font-medium"
            >
              ← Back to Home
            </button>
            <h1 className="text-2xl font-bold text-gray-900">User Profile</h1>
            <div></div>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white rounded-lg shadow-md p-8">
          {/* User Header */}
          <div className="flex items-center mb-8">
            <div className="w-24 h-24 bg-gray-300 rounded-full flex items-center justify-center mr-6">
              {user.profilePhoto ? (
                <img
                  src={user.profilePhoto}
                  alt={user.name}
                  className="w-24 h-24 rounded-full object-cover"
                />
              ) : (
                <span className="text-gray-600 font-semibold text-3xl">
                  {user.name.charAt(0).toUpperCase()}
                </span>
              )}
            </div>
            <div className="flex-1">
              <h2 className="text-3xl font-bold text-gray-900 mb-2">{user.name}</h2>
              {user.location && (
                <p className="text-gray-600 text-lg mb-4">{user.location}</p>
              )}
              <div className="flex items-center space-x-6 text-sm text-gray-600">
                <span>Rating: {user.rating.toFixed(1)} ⭐</span>
                <span>Total Swaps: {user.totalSwaps}</span>
              </div>
            </div>
            <button
              onClick={handleRequestSwap}
              className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 font-medium"
            >
              Request Swap
            </button>
          </div>

          {/* Skills Offered */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">Skills Offered</h3>
            <div className="flex flex-wrap gap-3">
              {user.skillsOffered.map((skill, index) => (
                <span
                  key={index}
                  className="bg-green-100 text-green-800 px-4 py-2 rounded-full text-sm font-medium"
                >
                  {skill}
                </span>
              ))}
              {user.skillsOffered.length === 0 && (
                <p className="text-gray-500">No skills offered yet</p>
              )}
            </div>
          </div>

          {/* Skills Wanted */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">Skills Wanted</h3>
            <div className="flex flex-wrap gap-3">
              {user.skillsWanted.map((skill, index) => (
                <span
                  key={index}
                  className="bg-blue-100 text-blue-800 px-4 py-2 rounded-full text-sm font-medium"
                >
                  {skill}
                </span>
              ))}
              {user.skillsWanted.length === 0 && (
                <p className="text-gray-500">No skills wanted yet</p>
              )}
            </div>
          </div>

          {/* Availability */}
          <div className="mb-8">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">Availability</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="flex items-center">
                <span className={`w-4 h-4 rounded mr-3 ${user.availability.weekends ? 'bg-green-500' : 'bg-gray-300'}`}></span>
                <span className="text-gray-700">Weekends</span>
              </div>
              <div className="flex items-center">
                <span className={`w-4 h-4 rounded mr-3 ${user.availability.evenings ? 'bg-green-500' : 'bg-gray-300'}`}></span>
                <span className="text-gray-700">Evenings</span>
              </div>
              <div className="flex items-center">
                <span className={`w-4 h-4 rounded mr-3 ${user.availability.weekdays ? 'bg-green-500' : 'bg-gray-300'}`}></span>
                <span className="text-gray-700">Weekdays</span>
              </div>
            </div>
            {user.availability.custom && (
              <div className="mt-4 p-3 bg-gray-50 rounded-lg">
                <p className="text-gray-700">{user.availability.custom}</p>
              </div>
            )}
          </div>

          {/* Profile Visibility */}
          <div className="border-t pt-6">
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">
                Profile Visibility: {user.profileVisibility}
              </span>
              <button
                onClick={handleRequestSwap}
                className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
              >
                Request Skill Swap
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileDetails;
