import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const HomePage = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [pagination, setPagination] = useState(null);
  const [loading, setLoading] = useState(true);
  const [searchSkill, setSearchSkill] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [stats, setStats] = useState({
    totalUsers: 1250,
    totalSwaps: 3420,
    activeUsers: 89
  });

  useEffect(() => {
    fetchUsers(currentPage, searchSkill);
    // Simulate real-time stats updates
    const interval = setInterval(() => {
      setStats(prev => ({
        ...prev,
        activeUsers: prev.activeUsers + Math.floor(Math.random() * 3) - 1
      }));
    }, 5000);
    return () => clearInterval(interval);
  }, [currentPage, searchSkill]);

  const fetchUsers = async (page = 1, skill) => {
    try {
      setLoading(true);
      // Simulate API call for demo
      setTimeout(() => {
        const mockUsers = [
          {
            _id: '1',
            name: 'Sarah Chen',
            location: 'Mumbai, India',
            profilePhoto: null,
            skillsOffered: ['UI/UX Design', 'Figma', 'Prototyping'],
            skillsWanted: ['React', 'JavaScript', 'Node.js'],
            rating: 4.8,
            totalSwaps: 23
          },
          {
            _id: '2',
            name: 'Rahul Sharma',
            location: 'Delhi, India',
            profilePhoto: null,
            skillsOffered: ['Python', 'Machine Learning', 'Data Analysis'],
            skillsWanted: ['Web Development', 'JavaScript', 'React'],
            rating: 4.6,
            totalSwaps: 18
          },
          {
            _id: '3',
            name: 'Priya Patel',
            location: 'Bangalore, India',
            profilePhoto: null,
            skillsOffered: ['Content Writing', 'SEO', 'Social Media'],
            skillsWanted: ['Graphic Design', 'Photoshop', 'Illustrator'],
            rating: 4.9,
            totalSwaps: 31
          }
        ];
        setUsers(mockUsers);
        setPagination({
          currentPage: page,
          totalPages: 5,
          totalUsers: 1250,
          hasNext: page < 5,
          hasPrev: page > 1
        });
        setLoading(false);
      }, 1000);
    } catch (error) {
      console.error('Error fetching users:', error);
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(1);
    fetchUsers(1, searchSkill);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleRequestSwap = (userId) => {
    navigate(`/user/${userId}`);
  };

  return (
    <div className="min-h-screen">
      {/* Animated Background */}
      <div className="fixed inset-0 gradient-bg opacity-20"></div>
      
      {/* Floating Elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-20 h-20 bg-blue-400 rounded-full opacity-20 animate-float"></div>
        <div className="absolute top-40 right-20 w-16 h-16 bg-purple-400 rounded-full opacity-20 animate-float" style={{animationDelay: '1s'}}></div>
        <div className="absolute bottom-20 left-20 w-24 h-24 bg-pink-400 rounded-full opacity-20 animate-float" style={{animationDelay: '2s'}}></div>
      </div>

      {/* Header */}
      <header className="modern-header relative z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-xl flex items-center justify-center">
                <span className="text-white font-bold text-xl">SS</span>
              </div>
              <h1 className="text-3xl font-bold text-white animate-slide-in-left">
                SkillSwap
              </h1>
            </div>
            <div className="flex items-center space-x-6">
              <button 
                onClick={() => navigate('/profile')}
                className="btn-secondary"
              >
                My Profile
              </button>
              <button 
                onClick={() => navigate('/swaps')}
                className="btn-secondary"
              >
                My Swaps
              </button>
              <button 
                onClick={() => navigate('/login')}
                className="btn-primary"
              >
                Login
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Hero Section */}
      <section className="relative z-10 py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12 animate-fade-in-up">
            <h2 className="text-5xl md:text-6xl font-bold text-white mb-6">
              Connect. Learn. 
              <span className="bg-gradient-to-r from-yellow-400 to-orange-500 bg-clip-text text-transparent"> Grow.</span>
            </h2>
            <p className="text-xl text-white/80 mb-8 max-w-3xl mx-auto">
              Join thousands of learners exchanging skills across India. Find your perfect skill swap partner and accelerate your learning journey.
            </p>
            
            {/* Stats Cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
              <div className="stats-card animate-fade-in-up" style={{animationDelay: '0.2s'}}>
                <div className="text-3xl font-bold text-blue-600 mb-2">{stats.totalUsers.toLocaleString()}+</div>
                <div className="text-white/80">Active Users</div>
              </div>
              <div className="stats-card animate-fade-in-up" style={{animationDelay: '0.4s'}}>
                <div className="text-3xl font-bold text-purple-600 mb-2">{stats.totalSwaps.toLocaleString()}+</div>
                <div className="text-white/80">Successful Swaps</div>
              </div>
              <div className="stats-card animate-fade-in-up" style={{animationDelay: '0.6s'}}>
                <div className="text-3xl font-bold text-pink-600 mb-2">{stats.activeUsers}</div>
                <div className="text-white/80">Online Now</div>
              </div>
            </div>
          </div>

          {/* Search Bar */}
          <div className="max-w-2xl mx-auto mb-16 animate-fade-in-up" style={{animationDelay: '0.8s'}}>
            <form onSubmit={handleSearch} className="relative">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search by skill (e.g., Photoshop, Excel, React, Guitar)"
                  value={searchSkill}
                  onChange={(e) => setSearchSkill(e.target.value)}
                  className="modern-input w-full pr-12 text-lg"
                />
                <button
                  type="submit"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 btn-primary py-2 px-4"
                >
                  🔍
                </button>
              </div>
            </form>
          </div>
        </div>
      </section>

      {/* Users Grid */}
      <section className="relative z-10 pb-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {loading ? (
            <div className="flex justify-center items-center py-20">
              <div className="loading-spinner"></div>
            </div>
          ) : (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {users.map((user, index) => (
                  <div 
                    key={user._id} 
                    className="modern-card hover-lift animate-fade-in-up"
                    style={{animationDelay: `${index * 0.1}s`}}
                  >
                    {/* User Header */}
                    <div className="flex items-center mb-6">
                      <div className="w-16 h-16 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center mr-4">
                        {user.profilePhoto ? (
                          <img
                            src={user.profilePhoto}
                            alt={user.name}
                            className="w-16 h-16 rounded-full object-cover"
                          />
                        ) : (
                          <span className="text-white font-bold text-xl">
                            {user.name.charAt(0).toUpperCase()}
                          </span>
                        )}
                      </div>
                      <div className="flex-1">
                        <h3 className="text-xl font-bold text-gray-900 mb-1">{user.name}</h3>
                        <p className="text-gray-600 text-sm">{user.location}</p>
                      </div>
                    </div>

                    {/* Skills Offered */}
                    <div className="mb-6">
                      <h4 className="font-semibold text-gray-900 mb-3 flex items-center">
                        <span className="w-2 h-2 bg-green-500 rounded-full mr-2"></span>
                        Skills Offered
                      </h4>
                      <div className="flex flex-wrap gap-2">
                        {user.skillsOffered.slice(0, 3).map((skill, skillIndex) => (
                          <span
                            key={skillIndex}
                            className="skill-tag"
                          >
                            {skill}
                          </span>
                        ))}
                        {user.skillsOffered.length > 3 && (
                          <span className="text-gray-500 text-sm">+{user.skillsOffered.length - 3} more</span>
                        )}
                      </div>
                    </div>

                    {/* Skills Wanted */}
                    <div className="mb-6">
                      <h4 className="font-semibold text-gray-900 mb-3 flex items-center">
                        <span className="w-2 h-2 bg-blue-500 rounded-full mr-2"></span>
                        Skills Wanted
                      </h4>
                      <div className="flex flex-wrap gap-2">
                        {user.skillsWanted.slice(0, 3).map((skill, skillIndex) => (
                          <span
                            key={skillIndex}
                            className="skill-tag"
                            style={{background: 'linear-gradient(45deg, #3b82f6, #1d4ed8)'}}
                          >
                            {skill}
                          </span>
                        ))}
                        {user.skillsWanted.length > 3 && (
                          <span className="text-gray-500 text-sm">+{user.skillsWanted.length - 3} more</span>
                        )}
                      </div>
                    </div>

                    {/* Stats */}
                    <div className="flex justify-between items-center mb-6 text-sm text-gray-600">
                      <span className="flex items-center">
                        ⭐ {user.rating.toFixed(1)}
                      </span>
                      <span>🔄 {user.totalSwaps} swaps</span>
                    </div>

                    {/* Request Button */}
                    <button
                      onClick={() => handleRequestSwap(user._id)}
                      className="btn-primary w-full"
                    >
                      Request Swap
                    </button>
                  </div>
                ))}
              </div>

              {/* Pagination */}
              {pagination && (
                <div className="flex justify-center items-center space-x-4 mt-12">
                  <button
                    onClick={() => handlePageChange(pagination.currentPage - 1)}
                    disabled={!pagination.hasPrev}
                    className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    ← Previous
                  </button>
                  
                  <div className="flex space-x-2">
                    {[...Array(pagination.totalPages)].map((_, i) => (
                      <button
                        key={i}
                        onClick={() => handlePageChange(i + 1)}
                        className={`w-10 h-10 rounded-full ${
                          pagination.currentPage === i + 1
                            ? 'bg-blue-600 text-white'
                            : 'bg-white/20 text-white hover:bg-white/30'
                        }`}
                      >
                        {i + 1}
                      </button>
                    ))}
                  </div>
                  
                  <button
                    onClick={() => handlePageChange(pagination.currentPage + 1)}
                    disabled={!pagination.hasNext}
                    className="btn-secondary disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Next →
                  </button>
                </div>
              )}
            </>
          )}
        </div>
      </section>

      {/* Footer */}
      <footer className="relative z-10 py-8 text-center text-white/60">
        <p>© 2024 SkillSwap - Connecting learners across India</p>
      </footer>
    </div>
  );
};

export default HomePage;
